<style>
/* Keyframe to transition through all the colors */
@keyframes rainbow {
    0% {
        fill: #ff0000;
    }
    16.666% {
        fill: #ff7f00;
    }
    33.333% {
        fill: #ffff00;
    }
    50% {
        fill: #00ff00;
    }
    66.666% {
        fill: #2525ff;
    }
    83.333% {
        fill: #6100a6;
    }
    100% {
        fill: #d000ff;
    }
}

/* Keyframe to bob up and down */
@keyframes bob {
    0% {
        transform: translateY(0);
    }
    50% {
        transform: translateY(-0.4rem);
    }
    100% {
        transform: translateY(0);
    }
}

#computer-logos path {
    animation: rainbow 10s infinite alternate;
}

#hacker-body {
    animation: bob 3s infinite alternate;
    filter: drop-shadow(1.5rem 1rem 1rem rgb(17, 17, 17));
}
</style>
